import {useState} from "react";
import {useEffect} from "react";

function EditPost(){


    const [message, setMessage] = useState("");
    const [img, setImg] = useState(null);

    /* get id from url */
    const url = window.location.href;
    console.log("url="+url);
    const urlstr = url.split("/");
    const id = urlstr.at(-1);
    console.log("id="+id);

    /* get token from storage */
    const token = JSON.parse(localStorage.getItem("mytoken"));
    //console.log("token = "+token);


    function handleEditedMessage(){

        // /* get id from url */
        // const url = window.location.href;
        // console.log("url="+url);
        // const urlstr = url.split("/");
        // const id = urlstr.at(-1);
        // console.log("id="+id);

        // /* get token from storage */
        // const token = JSON.parse(localStorage.getItem("mytoken"));
        // console.log("token = "+token);

        /* get data for PUT */

        let formdata = new FormData();
        let postObject;

        if(img !==null){
            postObject = {
                message:message
            }
            formdata.append("post", JSON.stringify(postObject));
            formdata.append("image", img);
        }
        else{
            postObject = {
                message:message,
                imageUrl:""
            }
            formdata.append("post", JSON.stringify(postObject));
        }


        fetch("http://localhost:5000/api/posts/"+id,{
            method: "PUT",

            headers:{
                "Authorization":"Bearer "+token,
                //"Content-Type":"application/json"
            },
            //body: JSON.stringify({message})
            body:formdata
        })
        .then(jsonResponse => jsonResponse.json())
        .then((res)=>{
            console.log(res.message)

            window.location= "/";
        })

        .catch(error => console.log("error = ", error));

    }

    const [post, setPost] = useState();

   async function getEditedPost(){
        await fetch("http://localhost:5000/api/posts/"+id,{
            method: "GET",

            headers:{
                "Authorization":"Bearer "+token,
                "Content-Type":"application/json"
            }
        })
        .then((res)=>{
            if(res.ok){
                return res.json();
            }
        })
        .then((data)=>{
            console.log(data);
            setPost(data);
            //message = post.message;
        })
        .catch(error => console.log("error = ", error));
    }

    //Does useEffect run after every render? Yes! 
    //By default, it runs both after the first render and after every update
    //https://reactjs.org/docs/hooks-effect.html

    useEffect(()=>{
        getEditedPost();
    },[]);

    //getEditedPost();



    return(
        <div>
        <h1>Editer votre message</h1>
        <div>
            <textarea 
                rows="12" 
                cols="80"
                onChange={(e)=> setMessage(e.target.value)}
                value = {post.message && message}
                //{post ? (value=post.message && {message}): (value={mesage})}
                //value={message}
                >
            </textarea>
            <p>{post.message}</p>
            <input 
                type="file"
                name="image"
                onChange={(e)=>{
                    setImg(e.target.files[0]);
                }}
                > 
            </input>
        </div>
        <div>
            <button type="button" onClick={handleEditedMessage}>Envoyer</button>
        </div>
        </div>
    )
}

export default EditPost;
