//import {useContext} from "react";
//import { UlogCtx } from "../appContext";
import {useEffect} from "react";
import {useState} from "react";
import {Link} from "react-router-dom";


import "./showPosts_style.css";
import jwt_decode from "jwt-decode";

function ShowPosts(){

    //const token = useContext(UlogCtx);
    //let count = useContext(UlogCtx);
    const [posts, setPosts] = useState([]);

    const token = JSON.parse(localStorage.getItem("mytoken"));
    //const userId = localStorage.getItem("userId");
    let userId = "";
    let isAdmin;
    if(token){
        const decodedToken = jwt_decode(token);
        userId = decodedToken.userId;
        isAdmin = decodedToken.isAdmin;
    };

   // const decodedToken = parseJwt(token);

    //console.log(`decoded token = ${decodedToken}`);

    useEffect(()=>{
        getAllPosts();
    },[]);

    function getAllPosts(){
        fetch("http://localhost:5000/api/posts",{
            method: "GET",
            headers: { 
                "Content-Type":"application/json",
                "Authorization":"Bearer "+token,
            }
        })
        .then((res)=>{
            if(res.ok){
                return res.json();
            }
        })
        .then((data)=>{
            console.log(data);
            setPosts(data);  // pass the data from the response to the state (var) posts 
        })
        .catch(error => console.log("error = ", error));
    } 


    function isUserlogged(){
        // if(token){
        //     return true;
        // }
        // else{
        //     return false;
        // }
        if(localStorage.length !==0){
            return true;
        }
        else{
            return false;
        }
    }

    const userLogged = isUserlogged();




    function deletePost(id){

        if(window.confirm("are you sure ?")){

        fetch("http://localhost:5000/api/posts/"+id,{
            method: "DELETE",
            headers: {
                "Authorization":"Bearer "+token,
                "Content-Type":"application/json"
            }
        })
        .then((res)=>{
                console.log(res.message)
                window.location.reload(true);

        })
        .catch(error => console.log("error delete = ", error))

        }
        else{
            console.log("you canceled");
        }

    }

    function showImages(){
        for(let i=0;i<posts.length;i++){
            if(posts[i].imageUrl !==""){
                return true;
            }
            else{
                return false;
            }
        }
    }

    //var count = 0;
    //const [likes, setLikes] = useState(""); 

    async function likePost(id,usersLiked){
        // count = 1 - count; 
        // //setLikes(count);
        // console.log(`count : ${count}`);
        // console.l    og(`id : ${id}`);
        let ulike = 0;
        //if(postUser_id===userId){
        if(usersLiked.length !==0){
            for(let i=0;i<usersLiked.length;i++){
                console.log(102);
                if(userId===usersLiked[i]){
                    ulike = 0;
                    console.log(`ulike 0 = ${ulike}`);
                }
                else{
                    ulike = 1;
                    console.log(`ulike 1 = ${ulike}`);

                }
            }
        }
        else{
            console.log(116);
            ulike = 1;
        }
        //}

        const postObject = {
            like: ulike,
            userId: userId
        }

        console.log(`postObject.like = ${postObject.like}`);

        await fetch("http://localhost:5000/api/posts/"+id+"/like",{
            method: "PUT",
            headers: {
                "Authorization":"Bearer "+token,
                "Content-Type":"application/json"
            },
            body: JSON.stringify(postObject)
        })
        // .then((res)=>{
        //     console.log(res.message)
        //     //window.location.reload(true);

        //  })
        .then(jsonResponse => jsonResponse.json())
        .then((res)=>{
            console.log(res.message);
            //getAllPosts();
            refetchData();
            //window.location="/";
        })
        .catch(error => console.log("error delete = ", error))

    }

   async function refetchData(){
        await getAllPosts();
   }

    return(
        <div>
            {/* <h1>Messages</h1> */}
            {/* <div>
            {posts.map((post)=> (
                <p>{post.message}</p>
            ))}

            </div> */}
            <div className="postContainer">
                {userLogged ? 
                    (<div>
                        {posts.slice(0).reverse().map((post)=> (
                            <div className="post" key={post._id}>
                                {showImages() ? 
                                    (<img className="post-images"src={post.imageUrl} alt=""></img>)
                                    :
                                    (<div></div>)}
                                    <p>{post.message}</p>
                                    {/* <p>identifiant du message :{post._id}</p>
                                    <p>identifiant du posteur :{post.userId}</p> */}

                                    {(userId === post.userId) || (isAdmin === true) ? 
                                        (<div className="postHud-owner">
                                            <div className="postHud-owner-actions">
                                                <Link to={`/editPost/${post._id}`}><i className="fa-regular fa-xl fa-pen-to-square"></i></Link>
                                                <button type="button" onClick={()=> {

                                                    deletePost(post._id)}}><i className="fa-solid fa-xl fa-trash-can"></i></button>
                                            </div>
                                            <div className="likeContainer">
                                                <i className="fa-regular fa-xl fa-heart" onClick={()=>likePost(post._id, post.usersLiked)}></i>
                                                <div className="likeCounter">
                                                    {post.likes}
                                                </div>
                                            </div>
                                        </div>)
                                        :
                                        (<div className="postHud-other">
                                            <div className="likeContainer">
                                                <i className="fa-regular fa-xl fa-heart" onClick={()=>likePost(post._id, post.usersLiked)}></i>
                                                <div className="likeCounter">{post.likes}</div>
                                            </div>
                                        </div>)} 
                            </div>
                        ))}
                    </div>)
                    :
                    (<div>deconnecté</div>)}
            </div>
        </div>
    );
}

export default ShowPosts;
