import "./newMessage_style.css";
import {useState} from "react";
//import {useContext} from "react";
//import { UlogCtx } from "../appContext";

function NewMessage(props){
    const isLoggedIn = props.isLoggedIn;
    const [message, setMessage] = useState("");
    const [img, setImg] = useState(null);
    //const token = useContext(UlogCtx);

    const token = JSON.parse(localStorage.getItem("mytoken"));

    //const formData = document.getElementById("imgForm");

    function handleMessage(){
        //console.log({message});
        //console.log("img = ", img);

        // let data = new FormData();
        // data.append("message",message);
        // data.append("imageUrl",img);
        // console.log("data = ", data);
        let data = {
            message:message
        }
        console.log("data stringified = "+ JSON.stringify(data));
        fetch("http://localhost:5000/api/posts",{
            method: "POST",
            headers: {

                "Authorization":"Bearer "+token,
                "Content-Type":"application/json"
            },
            body: JSON.stringify(data)
            //body: JSON.stringify({message:message})
            //body: JSON.stringify({message:message, imageUrl:img})
            //body: data

        })
        .then(jsonResponse => jsonResponse.json())
        .then((res)=>{
            console.log(res.message)
            window.location = "/";
        })
        .catch(error => console.log("error = ", error));

    }




    if(isLoggedIn){
        return(
            <div>
                <h2>Nouveau message</h2>
                <form id="imgForm">
                    <textarea 
                        rows="12" 
                        cols="80" 
                        onChange={(e)=> {setMessage(e.target.value)}}
                        value={message}
                        >

                    </textarea>
                    <div className="message-hud">

                            <input type="file"
                            onChange={(e)=>{
                                console.log("target = ", e.target.files[0]);
                                setImg(e.target.files[0]);
                                console.log("img = ", img)
                            }}
                            ></input>

                        <button type="button" onClick={handleMessage}>Envoyer</button>

                    </div>
                </form>
            </div>
        )
    }
    else{
        return(
            <div>Vous de devez vous connecter pour envoyer un message</div>
        )
    }
}

export default NewMessage;
