import "./newMessage_style.css";
import { useState } from "react";

import jwt_decode from "jwt-decode"

function NewMessage(props){

    const isLoggedIn = props.isLoggedIn;
    const [message, setMessage] = useState("");
    const [img, setImg] = useState(null);

    /* ================================== */
    /* get user info from session storage */
    /* ================================== */

    const token = JSON.parse(sessionStorage.getItem("mytoken"));

    // let userId = "";
    // let isAdmin;
    let userName;
    if(token){
        const decodedToken = jwt_decode(token);
        // userId = decodedToken.userId;
        // isAdmin = decodedToken.isAdmin;
        userName = decodedToken.email;
    };

    function handleMessage(){

        console.log("img depuis handleMessage = ", img);
        const postObject ={
            message:message,
            email:userName
        }
        const formData = new FormData();
        formData.append("post", JSON.stringify(postObject));
        formData.append('image', img);

        fetch("http://localhost:5000/api/posts",{
            method: "POST",
            headers: {

                "Authorization":"Bearer "+token,
                //"Content-Type":"application/json"
                //"Content-Type": "multipart/form-data"
            },
            body:formData

        })
        .then(jsonResponse => jsonResponse.json())
        .then((res)=>{
            console.log(res.message)
            window.location = "/";
        })
        .catch(error => console.log("error = ", error));

    }


    if(isLoggedIn){
        return(
            <div>
                <h2>Nouveau message</h2>

                <form id="imgForm">
                    <textarea 
                        name="message"
                        rows="12" 
                        cols="80" 
                        onChange={(e)=> {setMessage(e.target.value)}}
                        value={message}
                        >

                    </textarea>
                    <div className="message-hud">

                            <input type="file"
                            name="image"
                            onChange={(e)=>{
                                console.log("target = ", e.target.files[0]);
                                setImg(e.target.files[0]);
                                //setTimeout(function(){console.log("img = ", img)}, 1000); // debug
                            }}
                            ><i className="fa-solid fa-paperclip"></i></input>

                        <button type="button" onClick={handleMessage}>Envoyer</button>

                    </div>
                </form>
            </div>
        )
    }
    else{
        return(
            <div>Vous devez vous connecter pour envoyer un message</div>
        )
    }
}

export default NewMessage;
