import "./newMessage_style.css";
import {useState} from "react";
//import {useContext} from "react";
//import { UlogCtx } from "../appContext";

function NewMessage(props){
    const isLoggedIn = props.isLoggedIn;
    const [message, setMessage] = useState("");
    const [img, setImg] = useState("");
    //const token = useContext(UlogCtx);

    const token = JSON.parse(localStorage.getItem("mytoken"));

    function handleMessage(){
        //console.log({message});
        //console.log("img = ", img);
        fetch("http://localhost:5000/api/posts",{
            method: "POST",
            // withCredentials: true,
            // credentials: 'include',
            headers: {

                "Authorization":"Bearer "+token,
                "Content-Type":"application/json"
            },
            //body: JSON.stringify({message:message})
            body: JSON.stringify({message:message, imageUrl:img})
        })
        .then(jsonResponse => jsonResponse.json())
        .then((res)=>{
            console.log(res.message)
            window.location = "/";
        })
        .catch(error => console.log("error = ", error));

    }

    if(isLoggedIn){
        return(
            <div>
                <h2>Nouveau message</h2>
                <textarea 
                    rows="12" 
                    cols="80" 
                    onChange={(e)=> {setMessage(e.target.value)}}
                    value={message}
                    >

                </textarea>
                <div className="message-hud">
                    <input type="file"
                    onChange={(e)=>{
                        console.log("target = ", e.target.files[0].name);
                        setImg(e.target.files[0].name);
                        console.log("img = ", img)
                    }}
                    ></input>
                    <button type="button" onClick={handleMessage}>Envoyer</button>
                </div>
            </div>
        )
    }
    else{
        return(
            <div>Vous de devez vous connecter pour envoyer un message</div>
        )
    }
}

export default NewMessage;
